const ProductoEscalar = function (matrizA, e) {
    let matrizC = []
    for (let i = 0; i < matrizA.length; i++) {
        let matrizpC = []
        for (let j = 0; j < matrizA.length; j++) {
            matrizpC.push(e * matrizA[j][i])
        }
        matrizC.push(matrizpC);
    }
    return matrizC
}

module.exports = ProductoEscalar;







